package demopackage;

public class WhileDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int i=0;
		
		//to print 0 to 10
		while(i<=10)
			
		{
			System.out.println("the value is : "+i);
			i++;
		}
		
		//to print reverse numbers
		
		int j=100;
		
		while(j>0)
		
		{
			System.out.println("The Reverse Numbers the value is : "+j);
			j--;
		}
	}

}
